[3090] = {"Заплямований пергамент",{"О, {ім'я}! Поки тебе не було, мене попросили передати тобі цей пергамент.","Зазвичай я уникаю спілкування з чорнокнижниками, коли це можливо, але я думаю, що тобі слід прочитати його, як матимеш час. Якщо не помиляюсь, він від нового тренера, Нартока. Певно, ти захочеш поговорити з ним."},{"Прочитайте брудний пергамент і поговоріть з Нартоком у Лігві в долині Випробувань."}},
[3091] = {"Проста записка",{"Щойно посланниця шукала тебе, {ім'я}. Здається, її прислав тренер воїнів Гарутт. Якщо ця записка від Гарутта, я б не став зволікати з її прочитуванням."},{"Прочитайте просту записку та поговоріть із Гаруттом Громовим Рогом у таборі Нараче."}},
[3092] = {"Гравійована записка",{"Щойно посланниця шукала тебе, {ім'я}. Здається, її прислав тренер мисливців Ланка. Якщо ця записка від Ланка, я б не став зволікати з її прочитуванням."},{"Прочитайте гравійовану записку та поговоріть з Ланкою Далекострілом у плато Червоної Хмарини."}},
[3093] = {"Рунічна записка",{"Щойно посланниця шукала тебе, {ім'я}. Здається, її прислала тренерка шаманів Міла. Якщо ця записка від Міли, я б не став зволікати з її прочитуванням."},{"Прочитайте рунічну записку та поговоріть із Мілою Світанковою Мандрівницею в таборі Нараче."}},
[3094] = {"Вкрита мохом записка",{"Щойно посланниця шукала тебе, {ім'я}. Здається, її прислав тренер друїдів Ґарт. Якщо ця записка від Ґарта, я б не став зволікати з її прочитуванням."},{"Прочитайте вкриту мохом записку та поговоріть з Ґартом Мрякогоном у таборі Нараче."}},
[3095] = {"Простий сувій",{"А, ось і ти! Поки ти {стать:займався:займалася} бездумною Скарою, тобі принесли цей сувій. Думаю, це щось важливе — на ньому печатка тренера воїнів Даннала. Я б на твоєму місці його негайно прочитав."},{"Прочитайте простий сувій та поговоріть з Данналом Штерном в Смертедзвоні."}},
[3096] = {"Зашифрований сувій",{"А, ось і ти! Поки ти {стать:займався:займалася} бездумною Скарою, тобі принесли цей сувій. Думаю, це щось важливе — на ньому печатка тренера пройдисвітів Девіда Тріаса. Я б на твоєму місці його негайно прочитав."},{"Прочитайте зашифрований сувій та поговоріть з Девідом Тріасом в Смертедзвоні."}},
[3097] = {"Священний сувій",{"А, ось і ти! Поки ти {стать:займався:займалася} бездумною Скарою, тобі принесли цей сувій. Думаю, це щось важливе — на ньому печатка тренера жерців Дюстена. Я б на твоєму місці його негайно прочитав."},{"Прочитайте священний сувій та поговоріть з темним священником Дюстеном у церкві Смертодзвону."}},
[3098] = {"Гліфічний сувій",{"А, ось і ти! Поки ти {стать:займався:займалася} бездумною Скарою, тобі принесли цей сувій. Думаю, це щось важливе — на ньому печатка тренерки магів Ізабелли. Я б на твоєму місці його негайно прочитав."},{"Прочитайте гліфічний сувій та поговоріть з Ізабеллою у церкві Смертедзвону."}},
