[4802] = {"Е'ко зимокрайових",nil,nil},
[4803] = {"Е'ко щербозубів",nil,nil},
[4804] = {"Е'ко студовіїв",nil,nil},
[4805] = {"Е'ко льодореп'яхових",nil,nil},
