[9295] = {"Лист з фронту",{"Ви знайшли цей лист в одного з вбитих посіпак Скари. Судячи з вигляду та запаху, він був у невмерлого довгий час. Можливо, комусь в Каплиці Надії Світла буде цікаво поглянути..."},{"Віднесіть порваного листа Архіваріусу в Каплицю Надії Світла, що в Східних Зачумлених землях."}},
[9299] = {"Записка з фронту",{"Ви знайшли цю записку в одного з вбитих посіпак Скари. Судячи з вигляду та запаху, вона була у невмерлого довгий час. Можливо, комусь в Каплиці Надії Світла буде цікаво поглянути..."},{"Віднесіть потерту записку Архіваріусу в Каплицю Надії Світла, що в Східних Зачумлених землях."}},
[9300] = {"Сторінка з фронту",{"Ви знайшли цю сторінку в одного з вбитих посіпак Скари. Судячи з вигляду та запаху, вона була у невмерлого довгий час. Можливо, комусь в Каплиці Надії Світла буде цікаво поглянути..."},{"Віднесіть пошарпану сторінку Архіваріусу в Каплицю Надії Світла, що в Східних Зачумлених землях."}},
[9301] = {"Конверт з фронту",{"Ви знайшли цей конверт в одного з вбитих посіпак Скари. Судячи з вигляду та запаху, він був у невмерлого довгий час. Можливо, комусь в Каплиці Надії Світла буде цікаво поглянути..."},{"Віднесіть закривавлений конверт Архіваріусу в Каплицю Надії Світла, що в Східних Зачумлених землях."}},
[9302] = {"Послання з фронту",{"Ви знайшли це послання в одного з вбитих посіпак Скари. Судячи з вигляду та запаху, воно було у невмерлого довгий час. Можливо, комусь в Каплиці Надії Світла буде цікаво поглянути..."},{"Віднесіть зім'яте послання Архіваріусу в Каплицю Надії Світла, що в Східних Зачумлених землях."}},
[9304] = {"Документ з фронту",{"Ви знайшли цей лист в одного з вбитих посіпак Скари. Судячи з вигляду та запаху, він був у невмерлого довгий час. Можливо, комусь в Каплиці Надії Світла буде цікаво поглянути..."},{"Віднесіть вкритий брудом документ Архіваріусу в Каплицю Надії Світла, що в Східних Зачумлених землях."}},
[9310] = {"Тьмяний некротичний кристал",{"На трупі мертвої потвори ви бачите кристал, який ледь-ледь випромінює некротичну енергію."},{"Принесіть тьмяний некротичний кристал лейтенанту Рукаґу у внутрішньому дворі Підмістя."}},
[9317] = {"Освячені точильні камені",nil,nil},
[9318] = {"Благословенна чарівна олія",nil,nil},
[9319] = {"Світло у темних місцях",{"Ми не єдині, хто святкує це свято, {ім'я}. Усі створіння, усі культури осяяні полум'ям. Навіть у найтемніших місцях запалюються вогні.","Якщо ти бажаєш побачити їх особисто, йди в глибини. Знайди вогні, що горять у Стратгольмі, Школомансі, серед огрів Грізного Молота та у залах Чорноскельного шпиля. Я чекатиму на твоє повернення."},{"Знайдіть і доторкніться до вогнищ, розташованих у Чорноскельному шпилі, Грізному Молоті, Школомансі та Стратгольмі, а потім поверніться до хранителя полум'я фестивалю."}},
[9320] = {"Величезне зілля мани",nil,nil},
[9321] = {"Величезне зілля лікування",nil,nil},
[9322] = {"Дикі вогні Калімдору",{"Святкування вогняного фестивалю сонцестояння охоплює кожний куточок Азероту, і землі Калімдору не є винятком. Ти виглядаєш {стать:готовим:готовою} до подорожі — і, можливо, час відправлятися.","По справжньому насолодитися сонцестоянням можна, побачивши далекі блакитні вогні. Святкувальники запалили вогні на заході Зимоключа, на північному сході Азшари, біля Спочину Доблесті у пустелях Силітусу і біля входу в кратер Ун'Ґоро. Повертайся, коли закінчиш свою подорож."},{"Відвідайте табори сонцестояння, розташовані в Азшарі, Силітусі, кратері Ун'Ґоро та Зимоключі, а потім поверніться до хранителя полум'я фестивалю."}},
[9323] = {"Дикі вогні Східних Королівств",{"Наше свято полум'я розповсюджується по всій землі, {клас:к}. Немає жодного місця, яке б не отримало тепло цього свята, а найсильніші вогнища горять яскравим блакитним полум'ям. Знайди їх.","Одне з них знаходиться північніше Чорноскельного шпилю. Інші розташовані вглиб Східних Зачумлених земель біля покинутого міста, вздовж дороги до скель у Внутрішніх землях, і на шляху до Темного Порталу у Спустошених землях.","Повертайся, коли завершиш свою подорож."},{"Відвідайте табори сонцестояння, розташовані у Спустошених землях, Східних Зачумлених землях, Внутрішніх землях та Випаленій ущелині, а потім поговоріть з хранителем полум'я фестивалю."}},
[9324] = {"Викрадення полум'я Орґріммара",{"Це полум'я, викрадене із серця Орґріммара, переповнює вас теплом. Ця первісна сила чужа для вас, але знавець, можливо, знає більше..."},{"Принесіть полум'я Орґріммара до фестивального знавця."}},
[9325] = {"Викрадення полум'я Громового Бескиду",{"Це полум'я, викрадене у шаманів Громового Бескиду, переповнює вас теплом. Ця первісна сила чужа для вас, але знавець, можливо, знає більше..."},{"Принесіть полум'я Громового Бескиду до фестивального знавця."}},
[9326] = {"Викрадення полум'я Підмістя",{"Це полум'я, викрадене з руїн над Підмістям, переповнює вас теплом. Ця первісна сила чужа для вас, але знавець, можливо, знає більше..."},{"Принесіть полум'я Підмістя до фестивального знавця."}},
[9330] = {"Викрадення полум'я Штормовію",{"Це полум'я, викрадене із серця Штормовію, переповнює вас теплом. Ця первісна сила чужа для вас, але оповідник, можливо, знає більше..."},{"Принесіть полум'я Штормовію до фестивального оповідника."}},
[9331] = {"Викрадення полум'я Залізогарта",{"Це полум'я, викрадене із серця Залізогарта, переповнює вас теплом. Ця первісна сила чужа для вас, але оповідник, можливо, знає більше..."},{"Принесіть полум'я Залізогарта до фестивального оповідника."}},
